import './App.css';
import { useState } from 'react';
import Header from './components/layout/Header';
import Products from './pages/Products';

function App() {
  const [toggleCategory, setToggleCategory] = useState();
  const toggleCategoriesHandler = (e) => {
    setToggleCategory = e.target.textContent;
    console.log("vous avez le curseur sur une categorie");
  };

  return (
    <div className="App">
      <Header toggleCategoriesHandler={toggleCategoriesHandler} />
      <Products />
    </div>
  );
}

export default App;
